from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
left_drive = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_drive = Motor(Port.D, Direction.CLOCKWISE)
bily_bob_joe = Motor(Port.C, Direction.CLOCKWISE)
drive_base_2 = DriveBase(left_drive, right_drive, 55, 95)

def spin(distance):
    bily_bob_joe.run_angle(250, distance)


# The main program starts here.
drive_base_2.use_gyro(True)
print('Hello, Pybricks!')
drive_base_2.straight(570)
drive_base_2.turn(-45)
drive_base_2.straight(80)
drive_base_2.turn(90)
drive_base_2.straight(160)
spin(-600)
drive_base_2.straight(-160)
drive_base_2.turn(-90)
drive_base_2.straight(-80)
drive_base_2.turn(45)
drive_base_2.straight(-610)
