from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
left_drive = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_drive = Motor(Port.D, Direction.CLOCKWISE)
bily_bob_joe = Motor(Port.C, Direction.CLOCKWISE)
drive_base_2 = DriveBase(left_drive, right_drive, 55, 95)

async def lift(distance):
    await wait(0)
    await bily_bob_joe.run_angle(500, distance)

async def main():
    drive_base_2.use_gyro(True)
    print('FIRE IN THE HOLE!!!!!!')
    await multitask(
        lift(40),
        drive_base_2.straight(310),
    )
    await lift(-50)
    await drive_base_2.straight(-45)
    await multitask(
        lift(300),
        drive_base_2.turn(-90),
    )
    await drive_base_2.straight(170)
    await drive_base_2.turn(90)
    await drive_base_2.straight(210)
    await drive_base_2.straight(-600)


run_task(main())