from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
left_drive_2 = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_drive = Motor(Port.D, Direction.CLOCKWISE)
bily_bob_joe = Motor(Port.C, Direction.CLOCKWISE)
drive_base_2 = DriveBase(left_drive_2, right_drive, 55, 95)

async def LIFT(distance):
    await wait(0)
    await bily_bob_joe.run_angle(500, distance)

async def main():
    print('CLEAR THE SOIL!!!!!!')
    await multitask(
        LIFT(140),
        drive_base_2.straight(250),
    )
    await LIFT(-50)
    await multitask(
        drive_base_2.straight(-250),
        LIFT(-90),
    )


run_task(main())